mapper.select:
select order_seq, user_id, product_seq, order_status, quantity, product_name, order_date, ref, location, price
from delivery
where ref = #{ref} and user_id = #{userId}

mapper.update:
update Convenience
set conv_location = #{convLocation}, latitude = #{latitude}, longtitude = #{longtitude}
where conv_seq = #{convSeq}

update delivery
set ref = #{ref}
where ref = 0 and user_id = #{userId}

mapper.select:
select * from Delivery_list
where del_ref = #{delRef}
order by del_ref desc
