mapper.select:
SELECT count(*)
FROM PT
WHERE phone_num = #{phoneNumber}

mapper.update:
UPDATE PT
SET total_pt = total_pt + #{point}
WHERE phone_num = #{phoneNumber}

mapper.select:
SELECT total_pt
FROM PT
WHERE phone_num = #{phoneNumber}

mapper.update:
UPDATE PT
SET total_pt = total_pt - #{point}
WHERE phone_num = #{phoneNumber}
