mapper.select:
SELECT count(*)
FROM PT
WHERE phone_num = #{phoneNumber}

SELECT total_pt
FROM PT
WHERE phone_num = #{phoneNumber}

mapper.update:
UPDATE PT
SET total_pt = total_pt + #{point}
WHERE phone_num = #{phoneNumber}

mapper.select:
SELECT pt_seq AS ptSeq, phone_num AS phoneNumber, total_pt AS totalPoint, pwd 
FROM PT
WHERE phone_num = #{phoneNumber} and pwd = #{pwd}

mapper.update:
UPDATE PT
SET total_pt = total_pt - #{point}
WHERE phone_num = #{phoneNumber}
