mapper.select:
select MIN(product_seq) AS product_seq , MAX(conv_seq) AS conv_seq , MIN(category_id) AS category_id , product_name , MAX(product_roman_name) AS product_roman_name , MIN(price) AS price , MIN(price_origin) AS price_origin , MIN(price_discount) AS price_discount , SUM(stock_quantity) AS stock_quantity , MIN(expiration_date) AS expiration_date , MAX(discount_rate) AS discount_rate , MIN(promotion_info) AS promotion_info , MAX(barcode) AS barcode , MAX(img_url) AS img_url from Product
conv_seq = #{convSeq}
and product_name like concat('%', #{search}, '%')
and promotion_info = #{promotionInfo}
and category_id = #{categoryId}
and price between #{minPrice} and #{maxPrice}
and 1=0
and price >= #{minPrice}
GROUP BY product_name order by case when SUM(stock_quantity) = 0 then 1 else 0 end,
expiration_date asc
expiration_date desc
price_discount asc
price_discount desc
discount_rate desc
product_seq desc
product_seq asc
limit ${(pageNumber * pageSize)}, #{pageSize}

select * 
from Product 
where product_name = #{productName} and conv_seq = #{convSeq}

select SUM(stock_quantity) 
from Product 
where product_name = #{productName} and conv_seq = #{convSeq}

select * 
from Product 
where barcode = #{barcode} and conv_seq = #{convSeq}

select * from Call_product_conv
where conv_seq = #{convSeq} and call_status != -1
order by call_seq desc;

mapper.update:
update Call_product_conv
set amount = #{amount}, price = #{price}, price_origin = #{priceOrigin}
where product_name = #{productName} and call_ref = #{callRef} and conv_seq = #{convSeq} and call_status != -1;

update Call_product_conv
set call_ref = #{callRef}, call_status = 1
where call_ref = '0' and conv_seq = #{convSeq} and call_status != -1;

update Call_product_conv
set call_status = -1
where call_ref = #{callRef} and conv_seq = #{convSeq} and call_status != -1;

mapper.select:
select * from Call_product_conv_order_list
where call_ref = #{callRef} and conv_seq = #{convSeq}
order by call_ref desc
limit ${(pageNumber * pageSize)}, #{pageSize}

mapper.update:
UPDATE Call_product_conv_order_list
SET call_status=#{callStatus}, call_total_number=#{callTotalNumber}, call_total_price=#{callTotalPrice}, call_remark=#{callRemark}
WHERE call_ref = #{callRef} and conv_seq = #{convSeq} and call_status > 0

update Call_product_conv_order_list
set call_total_number=#{callTotalNumber}, call_total_price=#{callTotalPrice}
where call_ref = #{callRef} and conv_seq = #{convSeq} and call_status > 0

